SET SQL_MODE="NO_AUTO_VALUE_ON_ZERO";
CREATE TABLE IF NOT EXISTS `ad` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `name` varchar(256) NOT NULL,  `local_image_name` varchar(256) NOT NULL,  `remote_image_url` varchar(512) NOT NULL,  `image_height` int(11) NOT NULL DEFAULT '0',  `image_width` int(11) NOT NULL DEFAULT '0',  `text` text NOT NULL,  `link` varchar(256) NOT NULL,  `visible_in_sidebar` int(4) NOT NULL DEFAULT '1',  `visible_in_lists` int(4) NOT NULL DEFAULT '1',  `visible_on_review_page` int(4) NOT NULL DEFAULT '1',  PRIMARY KEY (`id`)) ENGINE=MyISAM  DEFAULT CHARSET=latin1 AUTO_INCREMENT=1 ;
CREATE TABLE IF NOT EXISTS `article` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `title` varchar(512) NOT NULL,  `seo_title` varchar(512) NOT NULL,  `description` text NOT NULL,  `meta_keywords` varchar(1024) NOT NULL,  `meta_description` varchar(1024) NOT NULL,  `link_text` varchar(512) NOT NULL,  `link_url` varchar(512) NOT NULL,  `last_modified` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,  PRIMARY KEY (`id`)) ENGINE=MyISAM  DEFAULT CHARSET=latin1 AUTO_INCREMENT=1 ;
CREATE TABLE IF NOT EXISTS `captcha` (  `captcha_id` bigint(13) unsigned NOT NULL AUTO_INCREMENT,  `captcha_time` int(10) unsigned NOT NULL,  `ip_address` varchar(16) NOT NULL DEFAULT '0',  `word` varchar(20) NOT NULL,  PRIMARY KEY (`captcha_id`),  KEY `word` (`word`)) ENGINE=InnoDB  DEFAULT CHARSET=latin1 AUTO_INCREMENT=1 ;
CREATE TABLE IF NOT EXISTS `category` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `name` varchar(255) NOT NULL,  `seo_name` varchar(255) NOT NULL,  `last_modified` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,  PRIMARY KEY (`id`)) ENGINE=MyISAM  DEFAULT CHARSET=latin1 AUTO_INCREMENT=1 ;
INSERT INTO `category` (`id`, `name`, `seo_name`, `last_modified`) VALUES(1, 'Main Category', 'main_category', '2011-11-22 13:31:01');
CREATE TABLE IF NOT EXISTS `category_default_feature` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `category_id` int(11) NOT NULL,  `feature_id` int(11) NOT NULL,  PRIMARY KEY (`id`)) ENGINE=MyISAM  DEFAULT CHARSET=latin1 AUTO_INCREMENT=1 ;
CREATE TABLE IF NOT EXISTS `category_default_rating` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `category_id` int(11) NOT NULL,  `rating_id` int(11) NOT NULL,  PRIMARY KEY (`id`)) ENGINE=MyISAM  DEFAULT CHARSET=latin1 AUTO_INCREMENT=1 ;
CREATE TABLE IF NOT EXISTS `comment` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `review_id` int(11) NOT NULL,  `quotation` varchar(512) NOT NULL,  `source` varchar(512) NOT NULL,  `site_link` varchar(512) NOT NULL,  `approved` int(4) NOT NULL DEFAULT '0',  `visitor_rating` int(11) NOT NULL,  `visitor_ip_address` varchar(40) NOT NULL,  `last_modified` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,  PRIMARY KEY (`id`)) ENGINE=MyISAM  DEFAULT CHARSET=latin1 AUTO_INCREMENT=1 ;
CREATE TABLE IF NOT EXISTS `feature` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `name` varchar(128) NOT NULL,  PRIMARY KEY (`id`)) ENGINE=MyISAM  DEFAULT CHARSET=latin1 AUTO_INCREMENT=1 ;
CREATE TABLE IF NOT EXISTS `page` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `name` varchar(512) NOT NULL,  `seo_name` varchar(512) NOT NULL,  `content` text NOT NULL,  `meta_keywords` varchar(1024) NOT NULL,  `meta_description` varchar(1024) NOT NULL,  `last_modified` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,  PRIMARY KEY (`id`)) ENGINE=MyISAM  DEFAULT CHARSET=latin1 AUTO_INCREMENT=3 ;
INSERT INTO `page` (`id`, `name`, `seo_name`, `content`, `meta_keywords`, `meta_description`, `last_modified`) VALUES(1, 'Privacy Policy & Terms And Conditions', 'privacy_policy_terms_and_conditions', '<p><strong><span style="text-decoration: underline; font-size: medium;">Privacy Policy &amp; Terms And Conditions</span></strong><br /><br /><span style="font-size: medium;">You can edit this page in Manager. Select &lsquo;Custom Pages&rsquo; from the menu.</span></p>', '', '', '0000-00-00 00:00:00'),(2, 'About Us', 'about_us', '<p><span style="text-decoration: underline;"><strong><span style="font-family: arial,helvetica,sans-serif; font-size: medium;">About Us</span></strong></span></p>\n<p><span style="font-family: arial,helvetica,sans-serif; font-size: medium;">You can edit this page in Manager. Select &lsquo;Custom Pages&rsquo; from the menu.</span></p>', '', '', '0000-00-00 00:00:00');
CREATE TABLE IF NOT EXISTS `rating` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `name` varchar(128) NOT NULL,  PRIMARY KEY (`id`)) ENGINE=MyISAM  DEFAULT CHARSET=latin1 AUTO_INCREMENT=1 ;
CREATE TABLE IF NOT EXISTS `review` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `tags` varchar(1024) NOT NULL,  `category_id` int(11) NOT NULL,  `featured` tinyint(4) NOT NULL DEFAULT '0',  `approved` int(4) NOT NULL DEFAULT '0',  `title` varchar(512) NOT NULL,  `seo_title` varchar(512) NOT NULL,  `description` text NOT NULL,  `image_name` varchar(512) DEFAULT NULL,  `image_extension` varchar(10) DEFAULT NULL,  `last_modified` timestamp NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,  `meta_keywords` varchar(1024) NOT NULL,  `meta_description` varchar(1024) DEFAULT NULL,  `vendor` varchar(128) DEFAULT NULL,  `link` varchar(512) DEFAULT NULL,  `clicks` int(11) NOT NULL DEFAULT '0',  `views` int(11) NOT NULL DEFAULT '0',  PRIMARY KEY (`id`),  UNIQUE KEY `seo_title` (`seo_title`),  FULLTEXT KEY `title` (`title`),  FULLTEXT KEY `titledesc` (`title`,`description`),  FULLTEXT KEY `tags` (`tags`)) ENGINE=MyISAM  DEFAULT CHARSET=latin1 AUTO_INCREMENT=1 ;
CREATE TABLE IF NOT EXISTS `review_feature` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `review_id` int(11) NOT NULL,  `feature_id` int(11) NOT NULL,  `value` varchar(512) NOT NULL,  PRIMARY KEY (`id`)) ENGINE=MyISAM  DEFAULT CHARSET=latin1 AUTO_INCREMENT=1 ;
CREATE TABLE IF NOT EXISTS `review_rating` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `review_id` int(11) NOT NULL,  `rating_id` int(11) NOT NULL,  `value` varchar(128) NOT NULL,  PRIMARY KEY (`id`)) ENGINE=MyISAM  DEFAULT CHARSET=latin1 AUTO_INCREMENT=1 ;
CREATE TABLE IF NOT EXISTS `session` (  `session_id` varchar(40) NOT NULL DEFAULT '0',  `ip_address` varchar(16) NOT NULL DEFAULT '0',  `user_agent` varchar(120) DEFAULT NULL,  `last_activity` int(10) unsigned NOT NULL DEFAULT '0',  `user_data` text NOT NULL,  PRIMARY KEY (`session_id`),  KEY `last_activity_idx` (`last_activity`)) ENGINE=MyISAM DEFAULT CHARSET=latin1;
CREATE TABLE IF NOT EXISTS `setting` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `name` varchar(255) NOT NULL,  `value` varchar(2048) NOT NULL,  PRIMARY KEY (`id`),  UNIQUE KEY `name` (`name`)) ENGINE=InnoDB  DEFAULT CHARSET=latin1 AUTO_INCREMENT=59 ;
