CREATE TABLE IF NOT EXISTS `tags` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `review_id` int(11) NOT NULL,  `tag` varchar(128) NOT NULL,  PRIMARY KEY (`id`)) ENGINE=MyISAM  DEFAULT CHARSET=latin1 AUTO_INCREMENT=1 ;
CREATE TABLE IF NOT EXISTS `user` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `name` varchar(255) NOT NULL,  `email` varchar(128) NOT NULL,  `password` varchar(255) NOT NULL,  `level` int(4) NOT NULL DEFAULT '1',  `key` varchar(64) DEFAULT NULL,  PRIMARY KEY (`id`),  UNIQUE KEY `email` (`email`)) ENGINE=MyISAM  DEFAULT CHARSET=latin1 AUTO_INCREMENT=1 ;